import React, { useState, useEffect } from 'react';
import * as foodApiFunctions from '../services/foodApiFunctions';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MainCards from '../components/MainCards';

function MainRecipes() {
  const [data, setData] = useState([]);
  const [ foodsToRender, setFoodsToRender ] = useState([{strMeal: "Corba", strMealThumb: "https://www.themealdb.com/images/media/meals/58oia61564916529.jpg"}]);
  const [ fetching, setLoading ] = useState(true);

  useEffect(() => {
    setLoading(true);
    foodApiFunctions.fetchAllFoodRecipes().then((response) => setData(response));
  }, []);

  useEffect(() => {
    setFoodsToRender(data.meals);
  }, [data]);

  useEffect(() => {
    setLoading(false);
  }, [foodsToRender]);

  const renderTwelveElements = () => {
    foodsToRender.map((food, index) => {
      if (index <= 12) {
        return <MainCards thumb={ food.strMealThumb } title={ food.strMeal } key={ index } />
      }
    });
  };
  return (
    <div>
      <Header title="Comidas" />
      {foodsToRender === undefined ? <p>Loading</p>  :     foodsToRender.map((food, index) => {
      if (index <= 11) {
        return <MainCards thumb={ food.strMealThumb } title={ food.strMeal } key={ index } />
      }
    })}
      <Footer />
    </div>
  );
}

export default MainRecipes;
