import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

function DoneRecipesCards({ thumb, title, index, id, type }) {
    const url = (text) => {
        switch (text) {
            case 'bebida':
                return 'bebidas';
                break;
                case 'comida':
                    return 'comidas';
                    break;
            default:
                return text;
                break;
        }
    }
  return (
    <Link to={ `/${url(type)}/${id}` }>
      <div data-testid={ `${index}-horizontal-image` }>
        <img
          src={ thumb }
          alt="Imagem do profile"
          data-testid={ `${index}-horizontal-image` }
        />
        <div>Category: <p data-testid={`${index}-horizontal-top-text`}>Colocar categoria</p></div>
        <div className="div" data-testid="page-div">
          <p data-testid={ `${index}-horizontal-name` }>{title}</p>
        </div>
        <div data-testid={`${index}-horizontal-share-btn`}>Ta faltando o bot√£o de compartilhar</div>
        <div>Feito em: <p data-testid={`${index}-horizontal-done-date`}>Colocar data</p></div>
        <div>tags: <p data-testid={`${index}-${'Pasta'}-horizontal-tag`}>Colocar tags</p></div>
      </div>
    </Link>
  );
}

DoneRecipesCards.propTypes = {
  title: PropTypes.string.isRequired,
  thumb: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired,
  id: PropTypes.string.isRequired,
  type: PropTypes.string.isRequired,
};

export default DoneRecipesCards;
