import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import clipboardCopy from 'clipboard-copy';
import shareIcon from '../images/shareIcon.svg';
import blackHearthIcon from '../images/blackHeartIcon.svg';
import whiteHearthIcon from '../images/whiteHeartIcon.svg';

function RecipesInProgressCard(
  { thumb, title, category, id, ingredients, instructions },
) {
  const [isFavorite, setIsFavorite] = useState(false);
  const [clipboard, setClipboard] = useState('');

  const handlerFavorite = () => {
    const favoriteRec = {
      id,
      type: 'comida',
      area: 'Italian',
      category,
      alcoholicOrNot: '',
      name: title,
      image: thumb,
    };
    localStorage.setItem('favoriteRecipes', JSON.stringify([favoriteRec]));
    localStorage.setItem('favorites', JSON.stringify(id));
    setIsFavorite(true);
  };

  const copyClipboard = async () => {
    const urlarray = window.location.href.split('/');
    const url = `${urlarray[0]}//${urlarray[2]}/comidas/${id}`;
    await clipboardCopy(url);
    setClipboard({ mensagem: 'Link copiado!' });
  };

  const handleCheckboxChange = (event) => {
    const { target } = event;
    target.checked = true;
  };

  return (
    <div className="main_recipe">
      <img
        data-testid="recipe-photo"
        alt="Imagem da comida"
        src={ thumb }
      />
      <h2
        data-testid="recipe-title"
      >
        {title}
      </h2>
      <input
        type="image"
        data-testid="share-btn"
        src={ shareIcon }
        alt="compartilhar"
        onClick={ copyClipboard }
      />
      {clipboard.mensagem}
      <button
        type="button"
        onClick={ handlerFavorite }
      >
        <img
          data-testid="favorite-btn"
          src={ isFavorite ? blackHearthIcon : whiteHearthIcon }
          alt="Icone Favoritar"
        />
      </button>
      <p
        data-testid="recipe-category"
      >
        {category}
      </p>
      <div>
        Ingredientes:
        {' '}
        {
          ingredients.map(
            (item, index) => (
            //   <span
            //     key={ index }
            //   >
            //     <p
            //       data-testid={ `data-testid=${index}-ingredient-step` }
            //     >
            //       { `${item.nomeIngrediente} ${item.medida}` }
            //     </p>
            //   </span>
                      <label key={ index }>
                      { `${item.nomeIngrediente} ${item.medida}` }
                      <input
                        name={`${index}-toDo`}
                        type="checkbox"
                        onChange={(e) => handleCheckboxChange(e)} 
                        />
                    </label>
            ),
          )
        }
      </div>
      <p
        data-testid="instructions"
      >
        {instructions}
      </p>
      <Link to="/receitas-feitas">
        <button
          className="iniciarReceita"
          type="button"
          data-testid="finish-recipe-btn"
        >
          Finalizar receita
        </button>
      </Link>
    </div>
  );
}

RecipesInProgressCard.propTypes = {
  title: PropTypes.string.isRequired,
  thumb: PropTypes.string.isRequired,
  id: PropTypes.string.isRequired,
  category: PropTypes.string.isRequired,
  ingredients: PropTypes.shape(PropTypes.string).isRequired,
  instructions: PropTypes.string.isRequired,
};

export default RecipesInProgressCard;
