import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

function DoneRecipesCards({ thumb, title, index, id, type, tags, category, doneDate, area, alcoholicOrNot }) {
  const url = (text) => {
    let returnText = '';
    switch (text) {
    case 'bebida':
      returnText = 'bebidas';
      break;
    case 'comida':
      returnText = 'comidas';
      break;
    default:
      returnText = text;
      break;
    }
    return returnText;
  };
//   const arrayOfTags = tags.split(',');
  return (
    <Link to={`/${url(type)}/${id}`}>
      <div>
        <img
          src={thumb}
          alt="Imagem do profile"
          data-testid={`${index}-horizontal-image`}
        />
        <div>
          Category:
          {' '}
          <p
            data-testid={ `${index}-horizontal-top-text` }
          >
            {`${area}${alcoholicOrNot} - ${category}`}
          </p>
        </div>
        <div className="div" data-testid="page-div">
          <p data-testid={`${index}-horizontal-name`}>{title}</p>
        </div>
        <div data-testid={`${index}-horizontal-share-btn`}>
          Ta faltando o bot√£o de compartilhar
        </div>
        <div>
          Feito em:{" "}
          <p data-testid={`${index}-horizontal-done-date`}>{doneDate}</p>
        </div>
        {tags.map((tag) => (
          <p data-testid={`${index}-${tag}-horizontal-tag`}>{tag}</p>
        ))}
      </div>
    </Link>
  );
}

DoneRecipesCards.propTypes = {
  title: PropTypes.string.isRequired,
  thumb: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired,
  id: PropTypes.string.isRequired,
  type: PropTypes.string.isRequired,
};

export default DoneRecipesCards;
