import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesContext from './RecipesContext';
import * as drinkApiFunctions from '../services/drinkApiFunctions';
import * as foodApiFunctions from '../services/foodApiFunctions';


function RecipesProvider({ children }) {
  const [foodsToRender, setFoodsToRender] = useState([]);
  const [drinksToRender, setDrinksToRender] = useState([]);
  const [login, setLogin] = useState({ email: '', password: '' });
  const [showSearchBar, setShowSearchBar] = useState(false);
  const [pathName, setPathName] = useState('');

  const toggleSearchBar = () => {
    setShowSearchBar(!showSearchBar);
  };

  const fetchDrinkByIngredients = (value) => {
    pathName === '/comidas' ?  foodApiFunctions.fetchFoodByIngredient(value).then((response) => setFoodsToRender(response.meals)) : drinkApiFunctions.fetchDrinkByIngredient(value).then((response) => setDrinksToRender(response.drinks));
  };

  const context = {
    login,
    setLogin,
    showSearchBar,
    toggleSearchBar,
    pathName,
    setPathName,
    foodsToRender,
    setFoodsToRender,
    drinksToRender,
    setDrinksToRender,
    fetchDrinkByIngredients,
  };

  return (
    <div>
      <RecipesContext.Provider value={ context }>
        {children}
      </RecipesContext.Provider>
    </div>
  );
}

RecipesProvider.propTypes = {
  children: PropTypes.oneOfType([PropTypes.object]).isRequired,
};

export default RecipesProvider;
