import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router';
import clipboardCopy from 'clipboard-copy';
import RecipesContext from '../context/RecipesContext';
import * as foodApiFunctions from '../services/foodApiFunctions';
import * as drinkApiFunctions from '../services/drinkApiFunctions';
import shareIcon from '../images/shareIcon.svg';
import blackHearthIcon from '../images/blackHeartIcon.svg';
import whiteHearthIcon from '../images/whiteHeartIcon.svg';
import RecipesInProgressCard from '../components/RecipesInProgressCard';

function RecipesInProgress() {
  // useParams, learned in https://medium.com/better-programming/using-url-parameters-and-query-strings-with-react-router-fffdcea7a8e9
  const { id, type } = useParams();
  const [foodObject, setFoodObject] = useState({});
  const [drinkObject, setDrinkObject] = useState({});
  const [allIngredients, setAllIngredients] = useState([]);
  const fifty = 50;
  useEffect(() => {
    if (type === 'comidas') {
      foodApiFunctions
        .fetchFoodDetailById(id)
        .then((response) => setFoodObject(...response.meals));
    } else {
      drinkApiFunctions
        .fetchDrinkDetailById(id)
        .then((response) => setDrinkObject(...response.drinks));
    }
  }, [id, type]);

  const createArrayOfIngredients = (object) => {
    const createdArrayOfIngredients = [];
    for (let i = zero; i <= fifty; i += 1) {
      if (object[`strIngredient${i}`]) {
        createdArrayOfIngredients.push(
          { nomeIngrediente: object[`strIngredient${i}`],
            medida: object[`strMeasure${i}`] },
        );
      }
    }
    setAllIngredients(createdArrayOfIngredients);
  };

  useEffect(() => {
    createArrayOfIngredients(foodObject);
  }, [foodObject]);

  useEffect(() => {
    createArrayOfIngredients(drinkObject);
  }, [drinkObject]);

  const renderCard = (object, arrayOfIngredients) => (
    <RecipesInProgressCard
      thumb={ type === 'comidas' ? object.strMealThumb : object.strDrinkThumb }
      title={ type === 'comidas' ? object.strMeal : object.strDrink }
      category={ type === 'comidas' ? object.strCategory : object.strCategory }
      id={ type === 'comidas' ? object.idMeal : object.idDrink }
      ingredients={ arrayOfIngredients }
    />
  );

  const switchRenderCard = () => {
    let card;
    switch (type) {
    case 'comidas':
      card = renderCard(foodObject, allIngredients);
      break;

    default:
      card = renderCard(drinkObject, allIngredients);
      break;
    }
    return card;
  };
  return (
    <div>{ switchRenderCard() }</div>
  );
}

export default RecipesInProgress;
