      <button
        type="button"
        key="all"
        data-testid="All-category-filter"
      >
        All
      </button>
    );
    finalArray.push(buttonAll);
    return finalArray;
  };
  const renderTwelveElements = (array) => {
    if (array === null) {
      return alert('Sinto muito, não encontramos nenhuma receita para esses filtros.');
