import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { Redirect } from 'react-router-dom';
import RecipesContext from '../context/RecipesContext';
import '../App.css';

function ExploreFoodIngredientICards(props) {
  const { title, index, path, history } = props;
  const { fetchInIngredientCard } = useContext(RecipesContext);
  const pathToRedirect = (pathName) => {
    if (pathName === '/explorar/comidas/ingredientes') {
      history.push('/comidas');
    }
    return history.push('/comidas');
  };

  return (
    <button type="button" onClick={ () => fetchInIngredientCard(title, path), pathToRedirect(path) }>
      <div data-testid={ `${index}-ingredient-card` }>
        <img
          src={ `https://www.themealdb.com/images/ingredients/${title}.png` }
          alt="Imagem do profile"
          data-testid={ `${index}-card-img` }
        />
        <div className="div" data-testid="page-div">
          <p data-testid={ `${index}-card-name` }>{title}</p>
        </div>
      </div>
    </button>
  );
}

ExploreFoodIngredientICards.propTypes = {
  title: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired,
  path: PropTypes.string.isRequired,
};

export default ExploreFoodIngredientICards;
