import React, { useState, useEffect } from 'react';
import * as foodApiFunctions from '../services/foodApiFunctions';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MainCards from '../components/MainCards';

function MainRecipes() {
  const [data, setData] = useState([]);
  const [ foodsToRender, setFoodsToRender ] = useState([]);

  useEffect(() => {
    foodApiFunctions.fetchAllFoodRecipes().then((response) => setData(response));
  }, []);

  useEffect(() => {
    const newArray = [];
    data.meals.forEach((food, index) => {
      if (index <= 12) {
        newArray.push(food);
      }
    });
    setFoodsToRender(newArray);
  }, [data]);

  const renderTwelveElements = () => {
    foodsToRender.map((food, index) => {
      if (index <= 12) {
        return <MainCards thumb={ food.strMealThumb } title={ food.strMeal } key={ index } />
      }
    });
  };
  return (
    <div>
      <Header title="Comidas" />
      {foodsToRender.map((food, index) => <MainCards thumb={ food.strMealThumb } title={ food.strMeal } key={ index } />)}
      <Footer />
    </div>
  );
}

export default MainRecipes;
